import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Articles', exact: true },
    { path: '/how-it-works', label: 'How it works' },
    { path: '/sponsors', label: 'Sponsors' },
    { path: '/donate', label: 'Donate' }
  ];

  // Add debug logging
  console.log('Navigation rendering:', {
    currentPath: location.pathname,
    navItems: navItems.length,
    location
  });

  const isActive = (path, exact = false) => {
    if (exact) {
      return location.pathname === path;
    }
    return location.pathname.startsWith(path);
  };

  return (
    <nav className="absolute top-0 left-0 right-0 bg-slate-900/50 backdrop-blur-sm z-10 shadow-lg shadow-slate-900/20">
      <div className="max-w-7xl mx-auto px-4 py-4">
        <ul className="flex justify-end space-x-8">
          {navItems.map(({ path, label, exact }) => {
            // Add debug logging for each item
            console.log('Rendering nav item:', { path, label, exact });
            return (
              <li key={path} className="relative">
                <Link 
                  to={path}
                  className={`
                    text-white underline
                    ${isActive(path, exact) ? 'font-medium' : 'font-normal'}
                  `}
                >
                  {label}
                  {isActive(path, exact) && (
                    <span className="absolute -left-3 top-1/2 -translate-y-1/2 w-2 h-2 bg-emerald-400 rounded-full" />
                  )}
                </Link>
              </li>
            );
          })}
        </ul>
      </div>
    </nav>
  );
};

export default Navigation;
