// src/components/FilterSystem.js
import React from 'react';

const FilterSystem = ({ articles, onFilterChange }) => {
  const regions = [
    { value: 'all', label: 'All Regions' },
    { value: 'northAmerica', label: 'North America' },
    { value: 'europe', label: 'Europe' },
    { value: 'asia', label: 'Asia' },
    { value: 'africa', label: 'Africa' },
    { value: 'southAmerica', label: 'South America' },
    { value: 'oceania', label: 'Oceania' }
  ];

  return (
    <div className="mb-8">
      <select 
        onChange={(e) => {
          const value = e.target.value;
          const filtered = value === 'all' 
            ? articles 
            : articles.filter(article => article.fields.region === value);
          onFilterChange(filtered);
        }}
        className="px-4 py-2 rounded-lg border border-slate-200 bg-white text-slate-600"
      >
        {regions.map(region => (
          <option key={region.value} value={region.value}>
            {region.label}
          </option>
        ))}
      </select>
    </div>
  );
};

export default FilterSystem;
