import React, { useEffect, useState } from 'react';
import { Dialog } from '@headlessui/react';
import { useNavigate, useLocation } from 'react-router-dom';
import { 
  MapPinIcon, 
  ClockIcon, 
  ShareIcon, 
  BookmarkIcon,
  XMarkIcon,
  ChartBarIcon,
  NewspaperIcon,
  LightBulbIcon,
  HandRaisedIcon
} from '@heroicons/react/24/outline';
import { ChevronDownIcon, ChevronUpIcon } from '@heroicons/react/24/outline';

const StoryModal = ({ isOpen, onClose, story }) => {
  const [activeEntity, setActiveEntity] = useState(null);
  const [isSaved, setIsSaved] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const [isExpanded, setIsExpanded] = useState(false);

  // Helper function to truncate text
  const truncateText = (text, lines = 15) => {
    if (!text) return '';
    const words = text.split(' ');
    const truncated = words.slice(0, lines * 10).join(' '); // Approximate 10 words per line
    return truncated;
  };

  useEffect(() => {
    if (isOpen && story) {
      const slug = story.fields?.slug || story.sys.id;
      if (location.pathname !== `/stories/${slug}`) {
        navigate(`/stories/${slug}`, { replace: true });
      }
    }
  }, [isOpen, story, navigate, location]);

  const handleClose = (e) => {
    e?.preventDefault();
    onClose();
    if (location.pathname.includes('/stories/')) {
      navigate('/', { replace: true });
    }
  };

  if (!story) return null;

  const renderEntityTooltip = (entity) => (
    <div className="absolute z-50 bg-white rounded-lg shadow-xl p-4 w-72">
      <h4 className="font-medium text-slate-800 mb-2">{entity.fields.name}</h4>
      <p className="text-sm text-slate-600">{entity.fields.description}</p>
      {entity.fields.image && (
        <img 
          src={`https:${entity.fields.image.fields.file.url}`}
          alt={entity.fields.name}
          className="mt-2 w-full h-32 object-cover rounded"
        />
      )}
    </div>
  );

  return (
    <Dialog
      open={isOpen}
      onClose={handleClose}
      className="relative z-50"
    >
      <div className="fixed inset-0 bg-black/30" aria-hidden="true" />

      <div className="fixed inset-0">
        <Dialog.Panel className="w-full h-full bg-white">
          <div className="h-full overflow-y-auto">
            {/* Header */}
            <div className="sticky top-0 bg-white px-4 sm:px-6 py-4 border-b border-slate-200 flex justify-between items-center z-10">
              <div className="flex-1 pr-12">
                <Dialog.Title className="text-xl font-semibold text-slate-800">
                  {story.fields?.title}
                </Dialog.Title>
                <div className="flex items-center gap-4 mt-2 text-sm text-slate-500">
                  <span>{new Date(story.fields?.publicationDate).toLocaleString()}</span>
                  <span>•</span>
                  <span className="flex items-center gap-1">
                    <NewspaperIcon className="w-4 h-4" />
                    {story.fields?.sourceOutlet?.fields?.name}
                  </span>
                </div>
              </div>
              <button
                onClick={handleClose}
                className="absolute right-4 top-4 p-2 hover:bg-slate-100 rounded-full transition-colors"
                aria-label="Close modal"
              >
                <XMarkIcon className="w-6 h-6 text-slate-500" />
              </button>
            </div>



            <div className="px-4 sm:px-6 py-6 max-w-4xl mx-auto space-y-8">
               {/* Move summary up */}
        <div className="prose max-w-none">
          <p className="text-lg text-slate-600 leading-relaxed">
            {story.fields?.summary}
          </p>
        </div>

        {/* Main image */}
        {story.fields?.mainImage?.fields?.file?.url && (
          <img 
            src={`https:${story.fields.mainImage.fields.file.url}`}
            alt={story.fields.mainImage.fields.title || story.fields.title}
            className="w-full h-96 object-cover rounded-xl"
          />
        )}
              {/* Main image */}
              {story.fields?.mainImage?.fields?.file?.url && (
                <img 
                  src={`https:${story.fields.mainImage.fields.file.url}`}
                  alt={story.fields.mainImage.fields.title || story.fields.title}
                  className="w-full h-96 object-cover rounded-xl"
                />
              )}

              {/* Story metadata and actions */}
              <div className="flex items-center justify-between pb-4 border-b border-slate-200">
                <div className="flex items-center gap-4">
                  <div className="flex items-center gap-2 text-slate-600">
                    <MapPinIcon className="w-5 h-5" />
                    <span>{story.fields?.location || 'Global'}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <ChartBarIcon className="w-5 h-5 text-green-600" />
                    <span className="text-green-600 font-medium">
                      {story.fields?.sentimentType?.fields?.title || 'Neutral'}
                    </span>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button 
                    className="p-2 hover:bg-slate-100 rounded-full transition-colors"
                    aria-label="Share story"
                  >
                    <ShareIcon className="w-5 h-5 text-slate-600" />
                  </button>
                  <button 
                    className={`p-2 hover:bg-slate-100 rounded-full transition-colors ${isSaved ? 'text-green-600' : 'text-slate-600'}`}
                    onClick={() => setIsSaved(!isSaved)}
                    aria-label={isSaved ? 'Remove from saved stories' : 'Save story'}
                  >
                    <BookmarkIcon className="w-5 h-5" />
                  </button>
                </div>
              </div>

              {/* Main content */}
              <div className="prose max-w-none">
                <p className="text-lg text-slate-600 leading-relaxed">
                  {story.fields?.summary}
                </p>
                <div className="mt-6">
                  {story.fields?.fullText}
                </div>
              </div>

              {/* Why this matters */}
              <div className="bg-green-50 rounded-xl p-6">
                <h3 className="text-lg font-medium text-green-900 mb-2 flex items-center gap-2">
                  <LightBulbIcon className="w-5 h-5" />
                  Why this matters
                </h3>
                <p className="text-green-800">
                  {story.fields?.impact || 'Impact analysis not available'}
                </p>
              </div>

              {/* Multiple perspectives */}
              {story.fields?.perspectives && (
                <div className="bg-slate-50 rounded-xl p-6">
                  <h3 className="text-lg font-medium text-slate-800 mb-4">Different Perspectives</h3>
                  <div className="space-y-4">
                    {story.fields.perspectives.map((perspective, index) => (
                      <div key={index} className="bg-white rounded-lg p-4 shadow-sm">
                        <h4 className="font-medium text-slate-700 mb-2">{perspective.fields.viewpoint}</h4>
                        <p className="text-slate-600 text-sm">{perspective.fields.summary}</p>
                        <div className="mt-2 text-sm text-slate-500">
                          Source: {perspective.fields.source.fields.name}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Timeline */}
              {story.fields?.timelineEvents && (
                <div className="relative">
                  <h3 className="text-lg font-medium text-slate-800 mb-4 flex items-center gap-2">
                    <ClockIcon className="w-5 h-5 text-slate-600" />
                    Related Timeline
                  </h3>
                  <div className="space-y-4">
                    {story.fields.timelineEvents.map((event, index) => (
                      <div 
                        key={index}
                        className="relative pl-6 pb-4 border-l-2 border-slate-200 last:border-l-0"
                      >
                        <div className="absolute left-0 top-0 w-2 h-2 rounded-full bg-blue-600 -translate-x-[5px]" />
                        <div className="text-sm text-slate-500 mb-1">
                          {new Date(event.fields.date).toLocaleDateString()}
                        </div>
                        <h4 className="font-medium text-slate-700 mb-1">{event.fields.title}</h4>
                        <p className="text-slate-600 text-sm">{event.fields.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Source credibility */}
              <div className="bg-slate-50 rounded-xl p-6">
                <h3 className="text-lg font-medium text-slate-800 mb-4 flex items-center gap-2">
                  <HandRaisedIcon className="w-5 h-5" />
                  Source Information
                </h3>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-slate-600">Credibility Score</span>
                    <span className="font-medium text-slate-800">
                      {story.fields?.sourceOutlet?.fields?.score || 'N/A'}
                    </span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-slate-600">Bias Rating</span>
                    <span className="px-3 py-1 bg-stone-100 rounded-full text-sm">
                      {story.fields?.sourceOutlet?.fields?.biasRating?.fields?.title || 'Unknown'}
                    </span>
                  </div>
                  <p className="text-sm text-slate-600">
                    {story.fields?.sourceOutlet?.fields?.description}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </Dialog.Panel>
      </div>
    </Dialog>
  );
};

export default StoryModal;
