import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import StorySentimentFilter from '../components/StorySentimentFilter';
import StoryDisplay from '../components/StoryDisplay';
import StoryModal from '../components/StoryModal';

const Stories = ({ selectedSentiment, setSelectedSentiment, filteredStories }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStory, setSelectedStory] = useState(null);
  const { slug } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  // Handle story selection
  const handleStorySelect = (story) => {
    const storySlug = story.fields?.slug || story.sys.id;
    setSelectedStory(story);
    setIsModalOpen(true);
    navigate(`/stories/${storySlug}`, { replace: true });
  };

  // Handle modal close
  const handleModalClose = () => {
    setIsModalOpen(false);
    setSelectedStory(null);
    navigate('/', { replace: true });
  };

  return (
    <div className="m-6 px-0">
      <StorySentimentFilter 
        selectedSentiment={selectedSentiment}
        setSelectedSentiment={setSelectedSentiment}
      />

      <StoryDisplay 
        stories={filteredStories}
        selectedSentiment={selectedSentiment}
        onStorySelect={handleStorySelect}
      />

      <StoryModal 
        isOpen={isModalOpen}
        onClose={handleModalClose}
        story={selectedStory}
      />
    </div>
  );
};

export default Stories;
