import React, { useState, useEffect } from 'react';
import { createClient } from 'contentful';
import ArticleDisplay from './components/ArticleDisplay';
import FilterSystem from './components/FilterSystem';
import ContinentsMap from './components/map/ContinentsMap';
import Footer from './components/Footer';
import './App.css';

const client = createClient({
  space: process.env.REACT_APP_CONTENTFUL_SPACE_ID,
  accessToken: process.env.REACT_APP_CONTENTFUL_ACCESS_TOKEN,
});

const Navigation = () => (
  <nav className="absolute bottom-0 right-0 p-6 z-10">
    <ul className="flex space-x-8">
      <li>
        <a href="#articles" className="text-white hover:text-slate-300 font-medium transition-colors">
          Articles
        </a>
      </li>
      <li>
        <a href="#about" className="text-white hover:text-slate-300 font-medium transition-colors">
          About
        </a>
      </li>
      <li>
        <a href="#donate" className="text-white hover:text-slate-300 font-medium transition-colors">
          Donate
        </a>
      </li>
    </ul>
  </nav>
);

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedSentiment, setSelectedSentiment] = useState('positive');
  const [filteredArticles, setFilteredArticles] = useState([]);

  const sentiments = [
    { id: 'positive', label: 'The good stuff', color: 'bg-[#35a236]' },
    { id: 'negative', label: 'The sad stuff', color: 'bg-amber-500' },
    { id: 'neutral', label: 'All the rest', color: 'bg-blue-500' }
  ];

  // Fetch articles on mount
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await client.getEntries({
          content_type: 'article',
          include: 2,
        });

        const articleData = response.items.map((item) => ({
          ...item,
          coordinates: item.fields.geographicLocation ? [
            parseFloat(item.fields.geographicLocation.split(", ")[1]),
            parseFloat(item.fields.geographicLocation.split(", ")[0]),
          ] : null
        }));

        setArticles(articleData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching articles:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  // Filter articles when selectedSentiment changes
  useEffect(() => {
    console.log('Filtering articles for sentiment:', selectedSentiment);
    const filtered = articles.filter(article => {
      const sentimentType = article.fields?.sentimentType?.fields?.title?.toLowerCase();
      console.log('Article sentiment:', sentimentType);
      return sentimentType === selectedSentiment;
    });
    console.log('Filtered articles count:', filtered.length);
    setFilteredArticles(filtered);
  }, [selectedSentiment, articles]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-stone-100">
        <div className="text-xl text-slate-300">Loading stories of progress...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-stone-100">
        <div className="text-xl text-red-400">Error: {error}</div>
      </div>
    );
  }

  const handleSentimentChange = (newSentiment) => {
    console.log('Setting new sentiment:', newSentiment);
    setSelectedSentiment(newSentiment);
  };

  return (
    <div className="flex flex-col min-h-screen bg-stone-100">
      {/* Hero Section */}
      <div className="relative h-96">
        <div 
          className="absolute inset-0 bg-cover bg-center"
          style={{ 
            backgroundImage: `url(/pastoral-landscape.jpg)`,
          }}
        />

        <Navigation />

        <div className="relative z-10 h-full flex justify-left m-10 pl-10">
          <div>
            <img 
              src="/logo.svg" 
              alt="TruGoodBeautiful Logo" 
              className="h-24 mx-auto filter drop-shadow-lg"
            />
            <p className="text-lg text-white drop-shadow m-2">
              Finding light in the headlines
            </p>
          </div>
        </div>
      </div>

      {/* Content Section */}
      <div className="flex-1">
        <div className="max-w-7xl mx-auto px-4">
          {/* Sentiment Filter Tabs */}
          <div className="-mt-10 flex justify-start space-x-4 py-4 border-b border-slate-300">
            {sentiments.map((sentiment) => (
              <button
                key={sentiment.id}
                onClick={() => handleSentimentChange(sentiment.id)}
                className={`px-6 py-3 rounded transition-all duration-300 font-medium
                  ${selectedSentiment === sentiment.id
                    ? `${sentiment.color} text-white shadow-md`
                    : 'bg-white text-slate-600 hover:bg-slate-50'
                  }`}
              >
                {sentiment.label}
              </button>
            ))}
          </div>

          {/* Map and Filters Section */}
          <div className="pt-4 mb-6">
            <div className="flex flex-col lg:flex-row gap-6">
              <div className="lg:w-1/2">
                {articles[0] && (
                  <div className="bg-white rounded-xl shadow-sm p-6">
                    <h2 className="text-xl font-semibold mb-4">Featured Story</h2>
                    <ArticleCard article={articles[0]} />
                  </div>
                )}
              </div>

              <div className="lg:w-1/2 flex flex-col">
                <div className="flex-grow bg-white rounded-xl shadow-sm p-6">
                  <ContinentsMap />
                </div>
                <div className="mt-6">
                  <FilterSystem 
                    articles={articles}
                    onFilterChange={(filtered) => setArticles(filtered)}
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Articles Grid */}
          <div className="mb-6">
            <ArticleDisplay articles={filteredArticles} />
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
