import React, { useEffect, useState } from 'react';
import { getPage, getSources } from '../lib/contentful';
import { documentToHtmlString } from '@contentful/rich-text-html-renderer';

const HowItWorks = () => {
  const [pageContent, setPageContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sources, setSources] = useState([]);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const [page, sourcesData] = await Promise.all([
          getPage('How it works'),
          getSources()
        ]);

        if (page?.fields) {
          setPageContent(page);
        }
        setSources(sourcesData);
      } catch (error) {
        console.error('Error fetching content:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <div className="animate-pulse flex justify-center">
          <div className="h-4 bg-slate-200 rounded w-24"></div>
        </div>
      </div>
    );
  }

  if (!pageContent) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <p className="text-center text-slate-600">Content not found</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-12">
      {/* Hero Section */}
      <div className="-mb-4">
        <div className="flex items-center gap-3">
          <div className="h-px flex-grow max-w-[100px] bg-emerald-500/30"></div>
          <h1 className="text-sm font-medium text-emerald-600 uppercase tracking-wider">
            {pageContent.fields.title}
          </h1>
        </div>
        <p className="text-4xl font-bold text-slate-800 mb-4 leading-tight max-w-2xl">
          Learn how we curate and analyze news to bring you the most balanced perspective
        </p>
      </div>

     {/* Main Content */}
     <div className="bg-white rounded-xl shadow-sm p-8 mb-8">
        <article className="prose prose-slate lg:prose-lg max-w-none flex justify-center">
          <div 
            dangerouslySetInnerHTML={{ 
              __html: documentToHtmlString(pageContent.fields.content)
            }}
            className="max-w-2xl w-full"
          />
        </article>
      </div>

      {/* Sources Section */}
      <div className="bg-white rounded-xl shadow-sm p-8">
        <h2 className="text-xl font-medium text-slate-700 mb-6">Our Sources</h2>
        <div className="overflow-hidden rounded-lg border border-slate-200">
          <table className="min-w-full divide-y divide-slate-200">
            <thead className="bg-stone-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-sm font-semibold text-slate-900">Source</th>
                <th scope="col" className="px-6 py-3 text-left text-sm font-semibold text-slate-900">Name & URL</th>
                <th scope="col" className="px-6 py-3 text-left text-sm font-semibold text-slate-900">Reliability</th>
                <th scope="col" className="px-6 py-3 text-left text-sm font-semibold text-slate-900">Bias Rating</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-200 bg-white">
              {sources.map((source) => (
                <tr key={source.sys.id} className="hover:bg-stone-50 transition-colors duration-150">
                  <td className="px-6 py-4 whitespace-nowrap">
                    {source.fields.logo && (
                      <img 
                        src={`https:${source.fields.logo.fields.file.url}`}
                        alt={source.fields.name}
                        className="h-8 w-auto object-contain"
                      />
                    )}
                  </td>
                  <td className="px-6 py-4">
                    <a 
                      href={source.fields.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="text-slate-900 hover:text-green-600 transition-colors duration-150"
                    >
                      {source.fields.name}
                    </a>
                  </td>
                  <td className="px-6 py-4 text-slate-600">
                    {source.fields.reliabilityScore}/10
                  </td>
                  <td className="px-6 py-4">
                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-stone-50 text-slate-800">
                      {source.fields.biasRating?.fields?.title || 'Unknown'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default HowItWorks;
