import React, { useEffect, useState } from 'react';
import { getPage } from '../lib/contentful';
import { documentToHtmlString } from '@contentful/rich-text-html-renderer';

const HowItWorks = () => {
  const [pageContent, setPageContent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const page = await getPage('How it works');
        if (page?.fields) {
          setPageContent(page);
        }
      } catch (error) {
        console.error('Error fetching How It Works content:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <p>Loading...</p>
      </div>
    );
  }

  if (!pageContent) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <p>Content not found</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-12">
      <h1 className="text-3xl font-semibold text-slate-800 mb-8">
        {pageContent.fields.title}
      </h1>

      <article className="prose prose-slate lg:prose-lg max-w-none">
        <div 
          dangerouslySetInnerHTML={{ 
            __html: documentToHtmlString(pageContent.fields.content)
          }}
        />
      </article>
    </div>
  );
};

export default HowItWorks;
