import React, { useState } from 'react';

const WorldMap = ({ articles, onRegionSelect }) => {
  const [highlightedRegion, setHighlightedRegion] = useState(null);

  // Count articles per region
  const articleCounts = articles.reduce((acc, article) => {
    const region = article.fields.geographicLocation;
    acc[region] = (acc[region] || 0) + 1;
    return acc;
  }, {});

  // Get color intensity based on article count
  const getRegionColor = (region) => {
    const count = articleCounts[region] || 0;
    const maxCount = Math.max(...Object.values(articleCounts));
    const intensity = count ? Math.max(0.3, count / maxCount) : 0.1;
    return `rgba(16, 185, 129, ${intensity})`; // emerald-500 with varying opacity
  };

  // Simplified region mapping
  const regions = {
    'North America': { path: 'M150,120 L250,120 L250,180 L150,180 Z', center: [200, 150] },
    'South America': { path: 'M200,200 L250,200 L250,280 L200,280 Z', center: [225, 240] },
    'Europe': { path: 'M300,120 L350,120 L350,160 L300,160 Z', center: [325, 140] },
    'Africa': { path: 'M300,180 L350,180 L350,250 L300,250 Z', center: [325, 215] },
    'Asia': { path: 'M380,120 L450,120 L450,200 L380,200 Z', center: [415, 160] },
    'Oceania': { path: 'M450,230 L500,230 L500,270 L450,270 Z', center: [475, 250] }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-8">
      <h2 className="text-xl font-medium mb-4">Global Story Distribution</h2>
      <div className="relative">
        <svg viewBox="0 0 600 400" className="w-full h-auto">
          {/* Simplified world map */}
          {Object.entries(regions).map(([name, region]) => (
            <g key={name}>
              <path
                d={region.path}
                fill={getRegionColor(name)}
                stroke="#e2e8f0"
                strokeWidth="1"
                onMouseEnter={() => setHighlightedRegion(name)}
                onMouseLeave={() => setHighlightedRegion(null)}
                onClick={() => onRegionSelect(name)}
                className="cursor-pointer transition-colors duration-300 hover:fill-emerald-600"
              />
              <text
                x={region.center[0]}
                y={region.center[1]}
                textAnchor="middle"
                className="text-xs fill-slate-700 pointer-events-none"
              >
                {name}
                {articleCounts[name] > 0 && ` (${articleCounts[name]})`}
              </text>
            </g>
          ))}
        </svg>

        {/* Tooltip */}
        {highlightedRegion && articleCounts[highlightedRegion] > 0 && (
          <div className="absolute top-0 left-0 bg-white shadow-lg rounded-lg p-3 text-sm">
            <h3 className="font-medium">{highlightedRegion}</h3>
            <p>{articleCounts[highlightedRegion]} stories</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default WorldMap;
