import React, { useEffect, useRef } from 'react';

function ContinentsMap() {
  const mapContainer = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.type = 'text/javascript';
    script.innerHTML = `
      $(document).ready(function(){

    // CSSMap;
    $("#map-continents").CSSMap({
      "size": 430,
      "mapStyle": "vintage",
      "tooltips": "sticky",
      "responsive": "auto",
      "multipleClick": {
        "enable": true,
        "searchUrl": "search.php",
        "searchLink": "",
        "searchLinkVar": "region",
        "separator": "+",
        "hideSearchLink": true,
        "clicksLimit": 0
      },
      "formSupport": {
        "enable": true,
        "inputId": "#demo-input",
        "selectId": "#demo-select",
        "selectLabel": "Location",
        "value": "name"
      }
    });  
  });
    `;

    mapContainer.current.appendChild(script);

    return () => {
      // Cleanup if needed
    };
  }, []);

  return (
   <div ref={mapContainer}> <div id="map-continents">
      <ul className="continents">
        <li className="c1"><a href="#africa">Africa</a></li>
        <li className="c2"><a href="#asia">Asia</a></li>
        <li className="c3"><a href="#australia">Australia</a></li>
        <li className="c4"><a href="#europe">Europe</a></li>
        <li className="c5"><a href="#north-america">North America</a></li>
        <li className="c6"><a href="#south-america">South America</a></li>
      </ul>
    </div>
    <div id="demo-form-fields" class="demo-form-fields" style="display: block;">
    <ul>
      <li>
        <label for="demo-select">Select dropdown</label>
           <div class="select-field">
            <select id="demo-select" name="demo-select" class="hidden-field">
             <option value="0">Location</option><option value="c1">Africa</option>
             <option value="c2">Asia</option><option value="c3">Australia</option>
             <option value="c4">Europe</option><option value="c5">North America</option>
             <option value="c6">South America</option></select>
            </div>
      </li>
      <li>
        <label for="demo-input">Input field</label>
        <input type="text" id="demo-input" name="demo-input" class="hidden-field"></input>
      </li>
    </ul>
  </div>
  </div>
  );
}

export default ContinentsMap;
