import React, { useState } from 'react';
import ArticleCard from './ArticleCard';

const ArticleDisplay = ({ articles }) => {
  const [displayCount, setDisplayCount] = useState(3);

  const displayedArticles = articles?.slice(0, displayCount);
  const hasMore = articles?.length > displayCount;

  const handleLoadMore = () => {
    setDisplayCount(prevCount => prevCount + 3);
  };

  return (
    <div className="mb-8">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {displayedArticles?.map((article) => (
          <ArticleCard key={article.sys.id} article={article} />
        ))}
      </div>

      {hasMore && (
        <div className="flex justify-center mt-8">
          <button
            onClick={handleLoadMore}
            className="px-6 py-3 bg-slate-100 hover:bg-slate-200 text-slate-600 rounded-xl 
              transition-all duration-300 font-medium shadow-sm hover:shadow-md"
          >
            Load more stories
          </button>
        </div>
      )}
    </div>
  );
};

export default ArticleDisplay;
