import React from 'react';

const Hero = () => (
  <div className="relative h-60 overflow-hidden">
    <div 
      className="absolute inset-0 bg-cover bg-bottom z-0"
      style={{ 
        backgroundImage: 'url("/new-landscape.jpg")',
        filter: 'brightness(0.4)'
      }}
    />
    <div className="pt-10 relative z-10 h-full flex justify-center items-center">
      <div className="text-center">
        <img 
          src="/logo.svg" 
          alt="TruGoodBeautiful Logo" 
          className="h-20 w-72 mx-auto filter object-contain" // Added w-44 and object-contain
        />
        <p className="text-sm text-white"> {/* Changed from text-lg to text-sm and mt-2 to mt-1 */}
          Finding light in the headlines
        </p>
      </div>
    </div>
  </div>
);

export default Hero;
