import React, { useState } from 'react';

const WorldMap = ({ articles, onRegionSelect }) => {
  const [highlightedRegion, setHighlightedRegion] = useState(null);

  // Count articles per region
  const articleCounts = articles.reduce((acc, article) => {
    const region = article.fields.geographicLocation;
    acc[region] = (acc[region] || 0) + 1;
    return acc;
  }, {});

  // More vibrant color palette
  const getRegionColor = (region) => {
    const count = articleCounts[region] || 0;
    const maxCount = Math.max(...Object.values(articleCounts), 1);
    const intensity = count ? Math.max(0.4, count / maxCount) : 0.2;

    const baseColors = {
      'North America': `rgba(147, 197, 253, ${intensity})`, // bright blue
      'South America': `rgba(167, 243, 208, ${intensity})`, // bright emerald
      'Europe': `rgba(253, 186, 116, ${intensity})`, // bright orange
      'Africa': `rgba(251, 207, 232, ${intensity})`, // bright pink
      'Asia': `rgba(252, 211, 77, ${intensity})`, // bright yellow
      'Oceania': `rgba(216, 180, 254, ${intensity})`, // bright purple
    };

    return baseColors[region] || `rgba(147, 197, 253, ${intensity})`;
  };

  return (
    <div className="bg-white rounded-xl shadow-md p-6 mb-8">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-medium text-slate-800 font-serif">Stories Around the World</h2>
      </div>

      <div className="relative w-full" style={{ maxHeight: '400px' }}>
        <svg 
          viewBox="0 0 1000 500" 
          className="w-full h-auto max-h-[400px]"
          style={{ backgroundColor: '#fef6e4' }} // soft cream background
        >
          {/* Decorative elements */}
          <g className="opacity-20">
            <path d="M0,180 Q500,150 1000,180" stroke="#94a3b8" fill="none" strokeWidth="1" />
            <path d="M0,220 Q500,250 1000,220" stroke="#94a3b8" fill="none" strokeWidth="1" />
          </g>

          {/* North America - more rounded, cloud-like shape */}
          <path
            d="M135,120 Q155,110 175,108 T215,106 T255,110 Q285,115 295,135 Q305,155 285,165 Q265,175 245,180 Q225,185 205,182 Q185,180 165,170 Q145,160 135,150 Q125,140 135,120 Z"
            fill={getRegionColor('North America')}
            stroke="#fff"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('North America')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('North America')}
            className="cursor-pointer transition-all duration-300 hover:scale-105"
          />

          {/* South America - leaf-like shape */}
          <path
            d="M235,230 Q245,235 255,240 Q275,250 265,270 Q255,290 245,300 Q235,310 225,315 Q215,310 205,300 Q195,290 200,270 Q205,250 220,240 Q230,235 235,230 Z"
            fill={getRegionColor('South America')}
            stroke="#fff"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('South America')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('South America')}
            className="cursor-pointer transition-all duration-300 hover:scale-105"
          />

          {/* Europe - cloud-like shape */}
          <path
            d="M420,120 Q440,115 460,110 Q480,108 490,115 Q505,120 505,125 Q505,135 495,145 Q485,150 465,150 Q445,150 435,145 Q425,140 420,135 Q415,130 420,120 Z"
            fill={getRegionColor('Europe')}
            stroke="#fff"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Europe')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Europe')}
            className="cursor-pointer transition-all duration-300 hover:scale-105"
          />

          {/* Africa - heart-like shape */}
          <path
            d="M435,180 Q450,175 465,175 Q480,175 495,185 Q510,195 505,215 Q500,235 485,245 Q470,255 455,255 Q440,255 435,245 Q430,235 425,215 Q420,195 435,180 Z"
            fill={getRegionColor('Africa')}
            stroke="#fff"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Africa')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Africa')}
            className="cursor-pointer transition-all duration-300 hover:scale-105"
          />

          {/* Asia - large cloud-like shape */}
          <path
            d="M525,115 Q575,110 625,125 Q645,135 645,145 Q645,165 635,175 Q615,185 595,185 Q575,185 565,175 Q545,170 535,165 Q525,155 525,145 Q515,135 525,115 Z"
            fill={getRegionColor('Asia')}
            stroke="#fff"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Asia')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Asia')}
            className="cursor-pointer transition-all duration-300 hover:scale-105"
          />

          {/* Oceania - small cloud clusters */}
          <path
            d="M665,245 Q680,250 695,255 Q700,265 685,275 Q675,280 665,280 Q655,275 655,265 Q655,255 665,245 Z"
            fill={getRegionColor('Oceania')}
            stroke="#fff"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Oceania')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Oceania')}
            className="cursor-pointer transition-all duration-300 hover:scale-105"
          />

          {/* Region Labels with decorative containers */}
          <g className="pointer-events-none">
            {Object.entries(articleCounts).map(([region, count]) => {
              const positions = {
                'North America': [215, 145],
                'South America': [235, 280],
                'Europe': [465, 125],
                'Africa': [465, 215],
                'Asia': [585, 145],
                'Oceania': [675, 265]
              };
              const [x, y] = positions[region] || [0, 0];

              return count > 0 ? (
                <g key={region}>
                  <text 
                    x={x} 
                    y={y} 
                    className="text-sm fill-slate-700 font-medium"
                    textAnchor="middle"
                  >
                    {region}
                    <tspan x={x} y={y + 15} className="text-xs">
                      {count} {count === 1 ? 'story' : 'stories'}
                    </tspan>
                  </text>
                </g>
              ) : null;
            })}
          </g>
        </svg>

        {/* Hover tooltip */}
        {highlightedRegion && articleCounts[highlightedRegion] > 0 && (
          <div className="absolute top-4 right-4 bg-white/90 backdrop-blur-sm shadow-lg rounded-xl p-3 text-sm border-2 border-blue-100">
            <h3 className="font-medium text-slate-800">{highlightedRegion}</h3>
            <p className="text-slate-600">
              {articleCounts[highlightedRegion]} {articleCounts[highlightedRegion] === 1 ? 'story' : 'stories'}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default WorldMap;
