import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Hero from './components/Hero';
import Navigation from './components/Navigation';
import Footer from './components/Footer';
import useArticles from './hooks/useArticles';
import useStories from './hooks/useStories';
import { layoutStyles } from './styles/layout';
import './App.css';

// Import page components
import Stories from './pages/Home';
import HowItWorks from './pages/HowItWorks';
import Sponsors from './pages/Sponsors';
import Shop from './pages/Shop';
import Sources from './pages/Sources';
import Contact from './pages/Contact';
import Articles from './pages/Articles';


function App() {
  // Articles state and data
  const { articles, loading: articlesLoading, error: articlesError } = useArticles();
  const [selectedSentiment, setSelectedSentiment] = useState('positive');

  // Stories state and data
  const { stories, loading: storiesLoading, error: storiesError } = useStories();
  const [storySelectedSentiment, setStorySelectedSentiment] = useState('positive');

  // Filter articles
  const filteredArticles = articles?.filter(article => {
    const sentimentType = article.fields?.sentimentType?.fields?.title;
    return selectedSentiment === 'positive' 
      ? sentimentType === 'positive'
      : ['neutral', 'negative'].includes(sentimentType);
  });

  // Filter stories
  const filteredStories = stories?.filter(story => {
    const sentiment = story.fields?.sentiment?.fields?.title;
    return storySelectedSentiment === 'positive' 
      ? sentiment === 'positive'
      : ['neutral', 'negative'].includes(sentiment);
  });

  // Show loading state if either articles or stories are loading
  if (articlesLoading || storiesLoading) {
    return (
      <div className={layoutStyles.loadingContainer}>
        <div className={layoutStyles.loadingText}>Loading stories of progress...</div>
      </div>
    );
  }

  // Show error state if either articles or stories have an error
  if (articlesError || storiesError) {
    return (
      <div className={layoutStyles.loadingContainer}>
        <div className={layoutStyles.errorText}>
          Error: {articlesError || storiesError}
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className={layoutStyles.container}>
        <Navigation />
        <Hero />

        <div className={layoutStyles.mainContent}>
          <Routes>
            <Route 
              path="/" 
              element={
                <Stories 
                  selectedSentiment={selectedSentiment}
                  setSelectedSentiment={setSelectedSentiment}
                  filteredArticles={filteredArticles}
                />
              } 
            />
            <Route path="/how-it-works" element={<HowItWorks />} />
            <Route path="/articles" element={<Articles />} />
            <Route path="/sponsors" element={<Sponsors />} />
            <Route path="/sources" element={<Sources />} />
            <Route path="/shop" element={<Shop />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </div>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
