import React, { useState } from 'react';
import Navigation from './components/Navigation';
import Hero from './components/Hero';
import SentimentFilter from './components/SentimentFilter';
import ArticleDisplay from './components/ArticleDisplay';
import Footer from './components/Footer';
import useArticles from './hooks/useArticles';
import { layoutStyles } from './styles/layout';
import './App.css';

function App() {
  const { articles, loading, error } = useArticles();
  const [selectedSentiment, setSelectedSentiment] = useState('positive');

  const filteredArticles = articles?.filter(article => {
    const sentimentType = article.fields?.sentimentType?.fields?.title;

    console.log('Article:', {
      title: article.fields?.pageName,
      sentiment: sentimentType,
      selected: selectedSentiment,
      wouldShow: selectedSentiment === 'positive' ? 
        sentimentType === 'Positive' : 
        ['Neutral', 'Negative'].includes(sentimentType)
    });

    return selectedSentiment === 'positive' 
      ? sentimentType === 'Positive'
      : ['Neutral', 'Negative'].includes(sentimentType);
  });

  console.log('Filter State:', {
    totalArticles: articles?.length,
    filteredCount: filteredArticles?.length,
    selectedSentiment,
  });

  if (loading) {
    return (
      <div className={layoutStyles.loadingContainer}>
        <div className={layoutStyles.loadingText}>
          Loading stories of progress...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={layoutStyles.loadingContainer}>
        <div className={layoutStyles.errorText}>
          Error: {error}
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <Navigation />
        <Hero />

        <div className="flex-grow">
          <div className="m-6 px-0">
            <SentimentFilter 
              selectedSentiment={selectedSentiment}
              setSelectedSentiment={setSelectedSentiment}
            />

            <ArticleDisplay 
              articles={filteredArticles} 
              selectedSentiment={selectedSentiment}
            />
          </div>
        </div>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
