import React, { useEffect, useState } from 'react';
import { Dialog } from '@headlessui/react';
import { useNavigate, useLocation } from 'react-router-dom';
import { MapPinIcon, ClockIcon, ShareIcon, BookmarkIcon } from '@heroicons/react/24/outline';

const StoryModal = ({ isOpen, onClose, story }) => {
  const [activeEntity, setActiveEntity] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    if (isOpen && story) {
      const slug = story.fields?.slug || story.sys.id;
      // Only update URL if not already on the story path
      if (location.pathname !== `/stories/${slug}`) {
        navigate(`/stories/${slug}`, { replace: true });
      }
    }
  }, [isOpen, story, navigate, location]);

  const handleClose = () => {
    onClose();
    navigate('/', { replace: true });
  };

  if (!story) return null;

  return (
    <Dialog
      open={isOpen}
      onClose={handleClose}
      className="relative z-50"
    >
      <div className="fixed inset-0 bg-black/30" aria-hidden="true" />

      <div className="fixed inset-0">
        <Dialog.Panel className="w-full h-full bg-white">
          <div className="h-full overflow-y-auto">
            {/* Header with close button */}
            <div className="sticky top-0 bg-white px-4 sm:px-6 py-4 border-b border-slate-200 flex justify-between items-center z-10">
              <h2 className="text-xl font-semibold text-slate-800 truncate pr-8">
                {story.fields?.title}
              </h2>
              <button
                onClick={handleClose}
                className="absolute right-4 top-4 p-2 hover:bg-slate-100 rounded-full transition-colors"
                aria-label="Close modal"
              >
                <svg className="w-6 h-6 text-slate-500" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Content */}
            <div className="px-6 py-4 space-y-6">
              {/* Main image */}
              {story.fields?.image?.fields?.file?.url && (
                <img 
                  src={`https:${story.fields.image.fields.file.url}`}
                  alt={story.fields.image.fields.title || story.fields.title}
                  className="w-full h-80 object-cover rounded-lg"
                />
              )}

              {/* Metadata */}
              <div className="flex flex-wrap gap-3">
                <span className="px-3 py-1 text-sm bg-stone-50 text-slate-600 rounded-full">
                  {story.fields?.categories?.[0]?.fields?.title || 'Uncategorized'}
                </span>
                <span className="text-green-600 text-sm font-medium flex items-center">
                  <span className="w-2 h-2 rounded-full bg-green-600 mr-2"></span>
                  {story.fields?.sentimentType?.fields?.title || 'Neutral'}
                </span>
              </div>

              {/* Story text */}
              <div className="prose max-w-none">
                <p className="text-slate-600">
                  {story.fields?.summary || 'No summary available'}
                </p>
              </div>

              {/* Why this matters */}
              <div className="bg-blue-50 p-4 rounded-lg">
                <h3 className="text-lg font-medium text-blue-900 mb-2">Why this matters</h3>
                <p className="text-blue-800">
                  {story.fields?.impact || 'Impact analysis not available'}
                </p>
              </div>

              {/* Timeline */}
              {story.fields?.relatedStories && (
                <div className="border-t border-slate-200 pt-4">
                  <h3 className="text-lg font-medium text-slate-800 mb-3">Related Stories</h3>
                  <div className="space-y-3">
                    {story.fields.relatedStories.map(relatedStory => (
                      <div key={relatedStory.sys.id} className="flex gap-3">
                        <div className="w-32 text-sm text-slate-500">
                          {new Date(relatedStory.fields.date).toLocaleDateString()}
                        </div>
                        <div className="flex-1">{relatedStory.fields.title}</div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Tags */}
              <div className="flex flex-wrap gap-2">
                {story.fields?.tags?.map(tag => (
                  <span key={tag.sys.id} className="px-3 py-1 text-sm bg-stone-50 text-slate-600 rounded-full">
                    {tag.fields.title}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </Dialog.Panel>
      </div>
    </Dialog>
  );
};

export default StoryModal;
