import React, { useEffect, useState } from 'react';
import { getPage, getSources } from '../lib/contentful';

const Sources = () => {
  const [pageContent, setPageContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sources, setSources] = useState([]);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const [page, sourcesData] = await Promise.all([
          getPage('How it works'),
          getSources()
        ]);

        if (page?.fields) {
          setPageContent(page);
        }
        setSources(sourcesData);
      } catch (error) {
        console.error('Error fetching content:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <div className="animate-pulse flex justify-center">
          <div className="h-4 bg-slate-200 rounded w-24"></div>
        </div>
      </div>
    );
  }

  if (!pageContent) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <p className="text-center text-slate-600">Content not found</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-4">
      <div className="bg-white rounded-xl shadow-sm p-4 sm:p-8">
        <h2 className="text-xl font-medium text-slate-700 mb-6">Our Sources</h2>

        {/* Mobile View: Card Layout */}
        <div className="block md:hidden space-y-4">
          {sources.map((source) => (
            <div key={source.sys.id} className="border border-slate-200 rounded-lg p-4">
              <div className="flex items-center gap-3 mb-2">
                {source.fields.logo && (
                  <img 
                    src={`https:${source.fields.logo.fields.file.url}`}
                    alt={source.fields.name}
                    className="h-6 w-auto object-contain"
                  />
                )}
                <a 
                  href={source.fields.url} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="text-slate-900 hover:text-green-600 transition-colors duration-150 font-medium"
                >
                  {source.fields.name}
                </a>
              </div>
              <p className="text-sm text-slate-600 mb-3">
                {source.fields.description}
              </p>
              <div className="flex items-center justify-between text-sm">
                <span className="text-slate-600">
                  Score: {source.fields.score}
                </span>
                <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-stone-50 text-slate-800">
                  {source.fields.biasRating?.fields?.title || 'Unknown'}
                </span>
              </div>
            </div>
          ))}
        </div>

        {/* Desktop View: Table Layout */}
        <div className="hidden md:block">
          <table className="min-w-full divide-y divide-slate-200 border border-slate-200 rounded-lg">
            <thead>
              <tr className="bg-stone-50">
                <th className="px-4 py-3 text-left text-xs uppercase tracking-wider font-medium text-slate-500 w-32">
                  Source
                </th>
                <th className="px-4 py-3 text-left text-xs uppercase tracking-wider font-medium text-slate-500">
                  Description
                </th>
                <th className="px-4 py-3 text-left text-xs uppercase tracking-wider font-medium text-slate-500 w-20">
                  Score
                </th>
                <th className="px-4 py-3 text-left text-xs uppercase tracking-wider font-medium text-slate-500 w-32">
                  AllSides bias
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-200 bg-white">
              {sources.map((source) => (
                <tr key={source.sys.id} className="hover:bg-blue-600/10 transition-colors duration-50">
                  <td className="px-4 py-4">
                    {source.fields.logo && (
                      <img 
                        src={`https:${source.fields.logo.fields.file.url}`}
                        alt={source.fields.name}
                        className="h-6 w-auto object-contain"
                      />
                    )}
                  </td>
                  <td className="px-4 py-4">
                    <div>
                      <a 
                        href={source.fields.url} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="text-slate-900 hover:text-green-600 transition-colors duration-150 font-medium"
                      >
                        {source.fields.name}
                      </a>
                      <p className="text-sm text-slate-600 mt-1">
                        {source.fields.description}
                      </p>
                    </div>
                  </td>
                  <td className="px-4 py-4 text-slate-600">
                    {source.fields.score}
                  </td>
                  <td className="px-4 py-4">
                    <span className="inline-flex items-center px-2 py-1 rounded-full text-sm font-medium bg-stone-50 text-slate-800">
                      {source.fields.biasRating?.fields?.title || 'Unknown'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Sources;
