import React, { useEffect, useState } from 'react';
import { Dialog } from '@headlessui/react';
import { useNavigate, useLocation } from 'react-router-dom';
import { 
  MapPinIcon, 
  ClockIcon, 
  ShareIcon, 
  BookmarkIcon 
} from '@heroicons/react/24/outline';
const StoryModal = ({ isOpen, onClose, story }) => {
  const [activeEntity, setActiveEntity] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    if (isOpen && story) {
      const slug = story.fields?.slug || story.sys.id;
      // Only update URL if not already on the story path
      if (location.pathname !== `/stories/${slug}`) {
        navigate(`/stories/${slug}`, { replace: true });
      }
    }
  }, [isOpen, story, navigate, location]);

  const handleClose = () => {
    onClose();
    navigate('/', { replace: true });
  };

  if (!story) return null;

  const renderEntityTooltip = (entity) => (
    <div className="absolute z-50 bg-white rounded-lg shadow-xl p-4 w-72">
      <h4 className="font-medium text-slate-800 mb-2">{entity.fields.name}</h4>
      <p className="text-sm text-slate-600">{entity.fields.description}</p>
      {entity.fields.image && (
        <img 
          src={`https:${entity.fields.image.fields.file.url}`}
          alt={entity.fields.name}
          className="mt-2 w-full h-32 object-cover rounded"
        />
      )}
    </div>
  );

  return (
    <Dialog
      open={isOpen}
      onClose={handleClose}
      className="relative z-50"
    >
      <div className="fixed inset-0 bg-black/30" aria-hidden="true" />

      <div className="fixed inset-0">
        <Dialog.Panel className="w-full h-full bg-white">
          <div className="h-full overflow-y-auto">
            {/* Existing header... */}

            <div className="px-6 py-4 space-y-6 max-w-4xl mx-auto">
              {/* Main image */}
              {story.fields?.mainImage?.fields?.file?.url && (
                <img 
                  src={`https:${story.fields.mainImage.fields.file.url}`}
                  alt={story.fields.mainImage.fields.title || story.fields.title}
                  className="w-full h-96 object-cover rounded-xl"
                />
              )}

              {/* Location and sharing toolbar */}
              <div className="flex items-center justify-between border-b border-slate-200 pb-4">
                <div className="flex items-center gap-2 text-slate-600">
                  <MapPinIcon className="w-5 h-5" />
                  <span>{story.fields?.location || 'Global'}</span>
                </div>
                <div className="flex gap-2">
                  <button className="p-2 hover:bg-slate-100 rounded-full transition-colors">
                    <ShareIcon className="w-5 h-5 text-slate-600" />
                  </button>
                  <button className="p-2 hover:bg-slate-100 rounded-full transition-colors">
                    <BookmarkIcon className="w-5 h-5 text-slate-600" />
                  </button>
                </div>
              </div>

              {/* Multiple perspectives */}
              {story.fields?.perspectives && (
                <div className="bg-slate-50 rounded-xl p-6">
                  <h3 className="text-lg font-medium text-slate-800 mb-4">Different Perspectives</h3>
                  <div className="space-y-4">
                    {story.fields.perspectives.map((perspective, index) => (
                      <div key={index} className="bg-white rounded-lg p-4 shadow-sm">
                        <h4 className="font-medium text-slate-700 mb-2">{perspective.fields.viewpoint}</h4>
                        <p className="text-slate-600 text-sm">{perspective.fields.summary}</p>
                        <div className="mt-2 text-sm text-slate-500">
                          Source: {perspective.fields.source.fields.name}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Timeline */}
              {story.fields?.timelineEvents && (
                <div className="relative">
                  <h3 className="text-lg font-medium text-slate-800 mb-4 flex items-center gap-2">
                    <ClockIcon className="w-5 h-5 text-slate-600" />
                    Timeline
                  </h3>
                  <div className="space-y-4">
                    {story.fields.timelineEvents.map((event, index) => (
                      <div 
                        key={index}
                        className="relative pl-6 pb-4 border-l-2 border-slate-200 last:border-l-0"
                      >
                        <div className="absolute left-0 top-0 w-2 h-2 rounded-full bg-blue-600 -translate-x-[5px]" />
                        <div className="text-sm text-slate-500 mb-1">
                          {new Date(event.fields.date).toLocaleDateString()}
                        </div>
                        <h4 className="font-medium text-slate-700 mb-1">{event.fields.title}</h4>
                        <p className="text-slate-600 text-sm">{event.fields.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}

            </div>
          </div>
        </Dialog.Panel>
      </div>
    </Dialog>
  );
};

export default StoryModal;
