import React, { useEffect, useRef } from 'react';

function ContinentsMap() {
  const mapContainer = useRef(null);

  useEffect(() => {
    // Wait for jQuery and CSSMap to be available
    if (window.jQuery && mapContainer.current) {
      window.jQuery("#map-continents").CSSMap({
        "size": 750,
        "tooltips": "visible",
        "responsive": "auto",
        "visibleList": {
          "enable": true,
          "listPosition": "bottom",
          "columns": 2,
          "columnsGap": 20,
          "columnWidth": 100,
        },
        "multipleClick": {
          "enable": true,
          "searchUrl": "search.php",
          "searchLink": "",
          "searchLinkVar": "region",
          "separator": "+",
          "hideSearchLink": true,
          "clicksLimit": 0
        }
      });
    }

    // Cleanup function
    return () => {
      if (window.jQuery && window.jQuery("#map-continents").data('cssMap')) {
        window.jQuery("#map-continents").data('cssMap').destroy();
      }
    };
  }, []);

  return (
    <div id="map-continents" ref={mapContainer}>
      <div className="continents">
        <ul className="continents-list">
          <li className="c1"><a href="#africa">Africa</a></li>
          <li className="c2"><a href="#asia">Asia</a></li>
          <li className="c3"><a href="#australia">Australia</a></li>
          <li className="c4"><a href="#europe">Europe</a></li>
          <li className="c5"><a href="#north-america">North America</a></li>
          <li className="c6"><a href="#south-america">South America</a></li>
        </ul>
      </div>
    </div>
  );
}

export default ContinentsMap;
