import React from 'react';
import { useEffect, useState } from 'react';
import { getPage } from '../lib/contentful';

const HowItWorks = () => {
  const [pageContent, setPageContent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const page = await getPage('how-it-works');
        if (page) {
          setPageContent(page);
        }
      } catch (error) {
        console.error('Error fetching How It Works content:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <p>Loading...</p>
      </div>
    );
  }

  if (!pageContent) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-12">
        <p>Content not found</p>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-12">
      <h1 className="text-3xl font-semibold text-slate-800 mb-6">
        {pageContent.fields.title}
      </h1>

      {pageContent.fields.heroImage?.fields?.file?.url && (
        <img
          src={`https:${pageContent.fields.heroImage.fields.file.url}`}
          alt={pageContent.fields.heroImage.fields.title || pageContent.fields.title}
          className="w-full h-64 object-cover rounded-xl mb-8 shadow-md"
        />
      )}

      <div className="prose prose-slate max-w-none">
        <div 
          dangerouslySetInnerHTML={{ 
            __html: pageContent.fields.content 
          }} 
          className="space-y-6 text-slate-600"
        />
      </div>
    </div>
  );
};

export default HowItWorks;
