import React, { useState, useEffect } from 'react';
import { createClient } from 'contentful';
import ArticleDisplay from './components/ArticleDisplay';
import FilterSystem from './components/FilterSystem';
import ContinentsMap from './components/map/ContinentsMap';
import './App.css';

const client = createClient({
  space: process.env.REACT_APP_CONTENTFUL_SPACE_ID,
  accessToken: process.env.REACT_APP_CONTENTFUL_ACCESS_TOKEN,
});

const Navigation = () => (
  <nav className="absolute top-0 right-0 p-6 z-10">
    <ul className="flex space-x-8">
      <li>
        <a href="#articles" className="text-white hover:text-slate-200 font-medium transition-colors">
          Articles
        </a>
      </li>
      <li>
        <a href="#about" className="text-white hover:text-slate-200 font-medium transition-colors">
          About
        </a>
      </li>
      <li>
        <a href="#donate" className="text-white hover:text-slate-200 font-medium transition-colors">
          Donate
        </a>
      </li>
    </ul>
  </nav>
);

function App() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        console.log('Fetching articles...');
        console.log('Space ID:', process.env.REACT_APP_CONTENTFUL_SPACE_ID);
        console.log('Access Token exists:', !!process.env.REACT_APP_CONTENTFUL_ACCESS_TOKEN);

        const response = await client.getEntries({
          content_type: 'article',
          include: 2,
        });
        console.log('Raw Contentful response:', response.items[0]); // Add this to see the structure


        console.log('Contentful response:', response);

        const articleData = response.items.map((item) => ({
          ...item,
          coordinates: item.fields.geographicLocation ? [
            parseFloat(item.fields.geographicLocation.split(", ")[1]),
            parseFloat(item.fields.geographicLocation.split(", ")[0]),
          ] : null,
          sentimentType: item.fields.sentimentType?.fields || { title: 'neutral' }
        }));

        console.log('Processed article data:', articleData);

        setArticles(articleData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching articles:', err);
        console.error('Error details:', err.message);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-900">
        <div className="text-xl text-slate-200">Loading stories of progress...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-900">
        <div className="text-xl text-red-400">Error: {error}</div>
      </div>
    );
  }

    return (
      <div className="min-h-screen bg-slate-900">
        {/* Hero Section - reduced height from 50vh to 30vh */}
        <div className="relative h-[30vh] overflow-hidden">
          <div 
            className="absolute inset-0 bg-cover bg-center"
            style={{ 
              backgroundImage: 'url(/pastoral-landscape.jpg)',
            }}
          />
          <div className="absolute inset-0 bg-gradient-to-b from-transparent via-transparent to-slate-900" />

          <Navigation />

          <div className="relative z-10 h-full flex items-center justify-center text-center">
            <div>
              {/* Reduced text size and margin */}
              <h1 className="text-4xl font-serif text-white mb-2 drop-shadow-lg">
                TruGoodBeautiful
              </h1>
              <p className="text-lg text-white drop-shadow">
                Illuminating Progress, Celebrating Solutions
              </p>
            </div>
          </div>
        </div>

        {/* Main Content - adjusted margins and padding */}
        <div className="max-w-7xl mx-auto px-4 -mt-16 relative z-10">
          {/* Featured Story and Map Section - reduced padding and margin */}
          <div className="bg-white/90 backdrop-blur-sm rounded-xl shadow-sm p-6 mb-6">
            <div className="flex flex-col lg:flex-row gap-6">
              <div className="lg:w-1/2">
                <h2 className="text-xl font-semibold mb-3">Today's Featured Story</h2>
                {articles[0] && (
                  <div>
                    <h3 className="text-lg mb-2">{articles[0].fields.pageName}</h3>
                    <p className="text-slate-600 text-sm">{articles[0].fields.summary}</p>
                  </div>
                )}
              </div>

              <div className="lg:w-1/2">
                <ContinentsMap />
              </div>
            </div>
          </div>

          {/* Articles Section - reduced padding */}
          <div className="bg-white/90 backdrop-blur-sm rounded-xl shadow-sm p-6">
            <FilterSystem 
              articles={articles}
              onFilterChange={(filtered) => setArticles(filtered)}
            />
            <ArticleDisplay articles={articles} />
          </div>
        </div>
      </div>
  );
}

export default App;
