import React, { useState } from 'react';

const WorldMap = ({ articles, onRegionSelect }) => {
  const [highlightedRegion, setHighlightedRegion] = useState(null);

  // Count articles per region
  const articleCounts = articles.reduce((acc, article) => {
    const region = article.fields.geographicLocation;
    acc[region] = (acc[region] || 0) + 1;
    return acc;
  }, {});

  // Bold, vibrant color palette
  const getRegionColor = (region) => {
    const count = articleCounts[region] || 0;
    const maxCount = Math.max(...Object.values(articleCounts), 1);
    const intensity = count ? Math.max(0.7, count / maxCount) : 0.3;

    const baseColors = {
      'North America': `rgba(37, 99, 235, ${intensity})`, // vivid blue
      'South America': `rgba(22, 163, 74, ${intensity})`, // vivid green
      'Europe': `rgba(236, 72, 153, ${intensity})`, // vivid pink
      'Africa': `rgba(234, 88, 12, ${intensity})`, // vivid orange
      'Asia': `rgba(168, 85, 247, ${intensity})`, // vivid purple
      'Oceania': `rgba(234, 179, 8, ${intensity})`, // vivid yellow
    };

    return baseColors[region] || `rgba(37, 99, 235, ${intensity})`;
  };

  return (
    <div className="bg-slate-900 rounded-lg shadow-xl p-6 mb-8">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-bold text-white">Stories Around the World</h2>
      </div>

      <div className="relative w-full" style={{ maxHeight: '400px' }}>
        <svg 
          viewBox="0 0 1000 500" 
          className="w-full h-auto max-h-[400px]"
          style={{ backgroundColor: '#0f172a' }} // slate-900
        >
          {/* Background grid for depth */}
          <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
            <path d="M 40 0 L 0 0 0 40" fill="none" stroke="rgba(255,255,255,0.05)" strokeWidth="1"/>
          </pattern>
          <rect width="1000" height="500" fill="url(#grid)" />

          {/* North America */}
          <path
            d="M135,120 Q155,110 175,108 T215,106 T255,110 Q285,115 295,135 Q305,155 285,165 Q265,175 245,180 Q225,185 205,182 Q185,180 165,170 Q145,160 135,150 Z"
            fill={getRegionColor('North America')}
            stroke="rgba(255,255,255,0.5)"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('North America')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('North America')}
            className="cursor-pointer transition-all duration-300 hover:brightness-110"
          />

          {/* South America */}
          <path
            d="M235,230 Q255,235 265,250 Q275,270 265,290 Q255,310 235,320 Q215,310 205,290 Q195,270 205,250 Q215,235 235,230 Z"
            fill={getRegionColor('South America')}
            stroke="rgba(255,255,255,0.5)"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('South America')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('South America')}
            className="cursor-pointer transition-all duration-300 hover:brightness-110"
          />

          {/* Europe */}
          <path
            d="M420,120 Q440,115 460,110 Q480,108 490,115 Q505,120 505,125 Q505,135 495,145 Q485,150 465,150 Q445,150 435,145 Q425,140 420,130 Z"
            fill={getRegionColor('Europe')}
            stroke="rgba(255,255,255,0.5)"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Europe')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Europe')}
            className="cursor-pointer transition-all duration-300 hover:brightness-110"
          />

          {/* Africa */}
          <path
            d="M435,180 Q450,175 465,175 Q480,175 495,185 Q510,195 505,215 Q500,235 485,245 Q470,255 455,255 Q440,255 435,245 Q430,235 425,215 Z"
            fill={getRegionColor('Africa')}
            stroke="rgba(255,255,255,0.5)"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Africa')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Africa')}
            className="cursor-pointer transition-all duration-300 hover:brightness-110"
          />

          {/* Asia */}
          <path
            d="M525,115 Q575,110 625,125 Q645,135 645,145 Q645,165 635,175 Q615,185 595,185 Q575,185 565,175 Q545,170 535,165 Q525,155 525,145 Z"
            fill={getRegionColor('Asia')}
            stroke="rgba(255,255,255,0.5)"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Asia')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Asia')}
            className="cursor-pointer transition-all duration-300 hover:brightness-110"
          />

          {/* Oceania */}
          <path
            d="M665,245 Q680,250 695,255 Q700,265 685,275 Q675,280 665,280 Q655,275 655,265 Q655,255 665,245 Z"
            fill={getRegionColor('Oceania')}
            stroke="rgba(255,255,255,0.5)"
            strokeWidth="2"
            onMouseEnter={() => setHighlightedRegion('Oceania')}
            onMouseLeave={() => setHighlightedRegion(null)}
            onClick={() => onRegionSelect('Oceania')}
            className="cursor-pointer transition-all duration-300 hover:brightness-110"
          />

          {/* Region Labels */}
          <g className="pointer-events-none">
            {Object.entries(articleCounts).map(([region, count]) => {
              const positions = {
                'North America': [215, 145],
                'South America': [235, 280],
                'Europe': [465, 125],
                'Africa': [465, 215],
                'Asia': [585, 145],
                'Oceania': [675, 265]
              };
              const [x, y] = positions[region] || [0, 0];

              return count > 0 ? (
                <g key={region}>
                  <text 
                    x={x} 
                    y={y} 
                    className="text-sm fill-white font-bold"
                    textAnchor="middle"
                  >
                    {region}
                    <tspan x={x} y={y + 15} className="text-xs fill-white opacity-80">
                      {count} {count === 1 ? 'story' : 'stories'}
                    </tspan>
                  </text>
                </g>
              ) : null;
            })}
          </g>
        </svg>

        {/* Hover tooltip */}
        {highlightedRegion && articleCounts[highlightedRegion] > 0 && (
          <div className="absolute top-4 right-4 bg-white shadow-xl rounded-lg p-3 text-sm border-2">
            <h3 className="font-bold text-slate-900">{highlightedRegion}</h3>
            <p className="text-slate-600">
              {articleCounts[highlightedRegion]} {articleCounts[highlightedRegion] === 1 ? 'story' : 'stories'}
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default WorldMap;
