import React, { useState, useEffect } from 'react';
import { createClient } from 'contentful';
import { Tab } from '@headlessui/react';
import ArticleCard from './components/ArticleCard';
import FilterSystem from './components/FilterSystem';
import ContinentsMap from './components/map/ContinentsMap';
import './App.css';

const client = createClient({
  space: process.env.REACT_APP_CONTENTFUL_SPACE_ID,
  accessToken: process.env.REACT_APP_CONTENTFUL_ACCESS_TOKEN,
});

const Navigation = () => (
  <nav className="absolute top-0 right-0 p-6 z-10">
    <ul className="flex space-x-8">
      <li>
        <a href="#articles" className="text-white hover:text-slate-200 font-medium transition-colors">
          Articles
        </a>
      </li>
      <li>
        <a href="#about" className="text-white hover:text-slate-200 font-medium transition-colors">
          About
        </a>
      </li>
      <li>
        <a href="#donate" className="text-white hover:text-slate-200 font-medium transition-colors">
          Donate
        </a>
      </li>
    </ul>
  </nav>
);

function App() {
  const [articles, setArticles] = useState([]);
  const [filteredArticles, setFilteredArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTab, setSelectedTab] = useState(0); // 0 = Good stuff (default)

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await client.getEntries({
          content_type: 'article',
          include: 2,
        });

        const articleData = response.items.map((item) => ({
          ...item,
          coordinates: [
            parseFloat(item.fields.geographicLocation.split(", ")[1]),
            parseFloat(item.fields.geographicLocation.split(", ")[0]),
          ],
          sentiment: item.fields.sentimentType.fields.title,
        }));

        setArticles(articleData);
        setFilteredArticles(articleData);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching articles:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  const categories = [
    { name: 'The good stuff', sentiment: 'positive' },
    { name: 'The sad stuff', sentiment: 'negative' },
    { name: 'All the rest', sentiment: 'neutral' }
  ];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-50">
        <div className="text-xl text-slate-600">Loading stories of progress...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-50">
        <div className="text-xl text-red-600">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Hero Section with Pastoral Background */}
      <div className="relative h-[50vh] overflow-hidden">
        <div 
          className="absolute inset-0 bg-cover bg-center"
          style={{ 
            backgroundImage: 'url(/pastoral-landscape.jpg)',
            backgroundPosition: 'center 60%'
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-b from-transparent via-transparent to-slate-50" />

        <Navigation />

        <div className="relative z-10 h-full flex items-center justify-center text-center">
          <div>
            <h1 className="text-5xl font-serif text-white mb-4 drop-shadow-lg">
              TruGoodBeautiful
            </h1>
            <p className="text-xl text-white drop-shadow">
              Illuminating Progress, Celebrating Solutions
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-6 -mt-20 relative z-10">
        {/* Featured Story and Map Section */}
        <div className="bg-white rounded-xl shadow-sm p-8 mb-12">
          <div className="flex flex-col lg:flex-row gap-8">
            {/* Featured Story */}
            <div className="lg:w-1/2">
              <h2 className="text-2xl font-semibold mb-4">Today's Featured Story</h2>
              {articles[0] && (
                <div>
                  <h3 className="text-xl mb-2">{articles[0].fields.title}</h3>
                  <p className="text-slate-600">{articles[0].fields.summary}</p>
                </div>
              )}
            </div>

            {/* Map */}
            <div className="lg:w-1/2">
              <ContinentsMap />
            </div>
          </div>
        </div>

        {/* Articles Section */}
        <div className="bg-white rounded-xl shadow-sm p-8">
          <Tab.Group selectedIndex={selectedTab} onChange={setSelectedTab}>
            <Tab.List className="flex space-x-4 border-b border-gray-200 mb-8">
              {categories.map((category) => (
                <Tab
                  key={category.name}
                  className={({ selected }) =>
                    `px-6 py-3 text-lg font-medium focus:outline-none whitespace-nowrap
                    ${selected 
                      ? 'text-emerald-600 border-b-2 border-emerald-600' 
                      : 'text-slate-500 hover:text-slate-700'
                    }`
                  }
                >
                  {category.name}
                </Tab>
              ))}
            </Tab.List>

            <Tab.Panels>
              {categories.map((category) => (
                <Tab.Panel key={category.sentiment}>
                  <FilterSystem 
                    articles={articles.filter(article => 
                      article.sentiment === category.sentiment
                    )} 
                    onFilterChange={setFilteredArticles}
                  />

                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {filteredArticles
                      .filter(article => article.sentiment === category.sentiment)
                      .map((article) => (
                        <ArticleCard key={article.sys.id} article={article} />
                      ))}
                  </div>
                </Tab.Panel>
              ))}
            </Tab.Panels>
          </Tab.Group>
        </div>
      </div>
    </div>
  );
}

export default App;
