import React, { useState } from 'react';
import ArticleCard from './ArticleCard';

const ArticleDisplay = ({ articles }) => {
  const [selectedSentiment, setSelectedSentiment] = useState('all');

  const sentiments = [
    { id: 'all', label: 'All Stories', color: 'bg-slate-500' },
    { id: 'positive', label: 'Positive', color: 'bg-emerald-500' },
    { id: 'neutral', label: 'Neutral', color: 'bg-blue-500' },
    { id: 'negative', label: 'Learning Opportunities', color: 'bg-amber-500' }
  ];

  // Debug information
  console.log('Articles received in ArticleDisplay:', articles);

  // Check if articles is an array and has items
  const hasArticles = Array.isArray(articles) && articles.length > 0;
  console.log('Has articles:', hasArticles);

  if (hasArticles) {
    // Log the first article's structure
    console.log('First article structure:', articles[0]);
  }

  return (
    <div className="min-h-screen bg-slate-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Debug information display */}
        <div className="mb-8 p-4 bg-white rounded-lg shadow">
          <h3 className="text-lg font-bold mb-2">Debug Information</h3>
          <p>Number of articles: {Array.isArray(articles) ? articles.length : 'Not an array'}</p>
          <p>Data type: {typeof articles}</p>
        </div>

        {/* Sentiment Filter Tabs */}
        <div className="flex justify-center space-x-4 mb-8">
          {sentiments.map((sentiment) => (
            <button
              key={sentiment.id}
              onClick={() => setSelectedSentiment(sentiment.id)}
              className={`px-4 py-2 rounded-full transition-all duration-300 ${
                selectedSentiment === sentiment.id
                  ? `${sentiment.color} text-white`
                  : 'bg-white text-slate-600 hover:bg-slate-100'
              }`}
            >
              {sentiment.label}
            </button>
          ))}
        </div>

        {/* Article Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {hasArticles ? (
            articles.map((article) => (
              <ArticleCard key={article.sys?.id} article={article} />
            ))
          ) : (
            <div className="col-span-3 text-center p-4">
              <p className="text-slate-600">No articles found</p>
              <pre className="mt-4 text-left bg-slate-100 p-4 rounded">
                {JSON.stringify(articles, null, 2)}
              </pre>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ArticleDisplay;
