import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import StorySentimentFilter from '../components/StorySentimentFilter';
import StoryDisplay from '../components/StoryDisplay';
import StoryModal from '../components/StoryModal';

const Stories = ({ selectedSentiment, setSelectedSentiment, filteredStories }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStory, setSelectedStory] = useState(null);
  const { slug } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  // Handle direct navigation and URL changes
  useEffect(() => {
    if (slug && filteredStories?.length) {
      const story = filteredStories.find(s => (s.fields?.slug || s.sys.id) === slug);
      if (story) {
        setSelectedStory(story);
        setIsModalOpen(true);
      } else {
        // Story not found, redirect to home
        navigate('/', { replace: true });
      }
    }
  }, [slug, filteredStories, navigate]);

  // Handle story selection
  const handleStorySelect = (story) => {
    const storySlug = story.fields?.slug || story.sys.id;
    setSelectedStory(story);
    setIsModalOpen(true);
    navigate(`/stories/${storySlug}`);
  };

  // Handle modal close
  const handleModalClose = () => {
    setIsModalOpen(false);
    setSelectedStory(null);
    navigate('/', { replace: true });
  };

  return (
    <div className="m-6 px-0">
      <StorySentimentFilter 
        selectedSentiment={selectedSentiment}
        setSelectedSentiment={setSelectedSentiment}
      />

      <StoryDisplay 
        stories={filteredStories}
        selectedSentiment={selectedSentiment}
        onStorySelect={handleStorySelect}
      />

      <StoryModal 
        isOpen={isModalOpen}
        onClose={handleModalClose}
        story={selectedStory}
      />
    </div>
  );
};

export default Stories;
