const Stories = ({ selectedSentiment, setSelectedSentiment, filteredStories }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStory, setSelectedStory] = useState(null);
  const { slug } = useParams();
  const location = useLocation();
  const navigate = useNavigate();

  // Check URL for story slug on mount and history changes
  useEffect(() => {
    if (slug && filteredStories) {
      const story = filteredStories.find(s => (s.fields?.slug || s.sys.id) === slug);
      if (story) {
        setSelectedStory(story);
        setIsModalOpen(true);
      }
    } else {
      // If no slug, ensure modal is closed
      setIsModalOpen(false);
      setSelectedStory(null);
    }
  }, [slug, filteredStories]);

  // Handle story selection
  const handleStorySelect = (story) => {
    const storySlug = story.fields?.slug || story.sys.id;
    setSelectedStory(story);
    setIsModalOpen(true);
    navigate(`/stories/${storySlug}`);
  };

  // Handle modal close
  const handleModalClose = () => {
    setIsModalOpen(false);
    setSelectedStory(null);
    navigate('/');
  };

  return (
    <div className="m-6 px-0">
      <StorySentimentFilter 
        selectedSentiment={selectedSentiment}
        setSelectedSentiment={setSelectedSentiment}
      />

      <StoryDisplay 
        stories={filteredStories}
        selectedSentiment={selectedSentiment}
        onStorySelect={handleStorySelect}
      />

      <StoryModal 
        isOpen={isModalOpen}
        onClose={handleModalClose}
        story={selectedStory}
      />
    </div>
  );
};
