import React, { useState, useEffect } from 'react';
import ArticleCard from './ArticleCard';
import FeaturedStory from './FeaturedStory';

const ArticleDisplay = ({ articles, selectedSentiment }) => {
  const [displayCount, setDisplayCount] = useState(3);
  const [displayedArticles, setDisplayedArticles] = useState([]);
  const [featuredArticle, setFeaturedArticle] = useState(null);

  // Set featured article and initial articles when articles array changes
  useEffect(() => {
    if (articles?.length > 0) {
      // For 'positive' sentiment, use first article as featured
      // For 'other' sentiment, use first article of filtered set
      const featured = articles[0];
      setFeaturedArticle(featured);

      // Set remaining articles for display, excluding featured
      const remaining = articles.slice(1, 4);
      setDisplayedArticles(remaining);
      setDisplayCount(3);
    } else {
      setFeaturedArticle(null);
      setDisplayedArticles([]);
    }
  }, [articles]);

  // Update displayed articles when count changes
  useEffect(() => {
    if (articles?.length > 1) {
      setDisplayedArticles(articles.slice(1, displayCount + 1));
    }
  }, [displayCount, articles]);

  // Debug logging
  useEffect(() => {
    console.log('ArticleDisplay State:', {
      selectedSentiment,
      featuredArticle: featuredArticle?.fields?.pageName,
      totalArticles: articles?.length,
      displayedCount: displayedArticles?.length,
      hasMore: articles?.length > (displayCount + 1)
    });
  }, [articles, displayCount, displayedArticles, featuredArticle, selectedSentiment]);

  const hasMore = articles?.length > (displayCount + 1);

  const handleLoadMore = () => {
    setDisplayCount(prev => prev + 3);
  };

  if (!articles) {
    return <div>Loading articles...</div>;
  }

  return (
    <div>
      {/* Featured Story Section */}
      {featuredArticle && (
        <div className="pt-4 pb-6">
          <FeaturedStory article={featuredArticle} />
        </div>
      )}

      {/* Article Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {displayedArticles.map((article) => (
          <ArticleCard 
            key={article.sys.id} 
            article={article} 
          />
        ))}
      </div>

      {/* Load More Button */}
      {hasMore && (
        <div className="flex justify-center mt-8">
          <button
            onClick={handleLoadMore}
            className="px-6 py-3 bg-stone-200 hover:bg-green-600 hover:text-white text-slate-600 rounded-xl 
              transition-all duration-100 font-medium shadow-md hover:shadow-lg"
          >
            Load more stories ({articles.length - (displayCount + 1)} remaining)
          </button>
        </div>
      )}
    </div>
  );
};

export default ArticleDisplay;
