import React, { useEffect, useRef } from 'react';

function ContinentsMap() {
  const mapContainer = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.type = 'text/javascript';

    // Match Tailwind's md breakpoint (768px)
    const mapSize = 768;

    script.innerHTML = `
      $(document).ready(function(){
        $("#map-continents").CSSMap({
          "size": 750,
          "tooltips": "visible",
          "responsive": {
            "min_window_width": ${mapSize}
          },
          "mobileSupport": true,
          "onLoad": function(e) {
            // Optional: Add any custom behavior on map load
            console.log('Map loaded successfully');
          },
          "onHover": function(e) {
            // Optional: Add custom hover behavior
          }
        });
      });
    `;

    mapContainer.current.appendChild(script);

    return () => {
      // Clean up script when component unmounts
      if (mapContainer.current) {
        const scripts = mapContainer.current.getElementsByTagName('script');
        for (let script of scripts) {
          script.remove();
        }
      }
    };
  }, []);

  return (
    <div id="map-continents" ref={mapContainer} className="cssmap-container">
      <ul className="continents">
        <li className="africa">
          <a href="#africa">Africa</a>
          <div className="tooltip"><span>Africa</span></div>
        </li>
        <li className="asia">
          <a href="#asia">Asia</a>
          <div className="tooltip"><span>Asia</span></div>
        </li>
        <li className="australia">
          <a href="#australia">Australia</a>
          <div className="tooltip"><span>Australia</span></div>
        </li>
        <li className="europe">
          <a href="#europe">Europe</a>
          <div className="tooltip"><span>Europe</span></div>
        </li>
        <li className="north-america">
          <a href="#north-america">North America</a>
          <div className="tooltip"><span>North America</span></div>
        </li>
        <li className="south-america">
          <a href="#south-america">South America</a>
          <div className="tooltip"><span>South America</span></div>
        </li>
      </ul>
    </div>
  );
}

export default ContinentsMap;
