import React from 'react';

const ArticleCard = ({ article }) => {
  const getBiasColor = (rating) => {
    const colors = {
      '1': 'text-blue-600',
      '2': 'text-blue-400',
      '3': 'text-slate-500',
      '4': 'amber-400',
      '5': 'text-amber-600'
    };
    return colors[rating] || 'text-slate-500';
  };

  return (
    <div className="bg-white rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow duration-300">
      <div className="relative h-48 overflow-hidden">
        <img
          src={article.image?.url || '/api/placeholder/400/320'}
          alt={article.title}
          className="w-full h-full object-cover"
        />
        <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/50 to-transparent p-4">
          <span className="text-white text-sm">{article.geographicLocation}</span>
        </div>
      </div>

      <div className="p-6">
        <div className="flex justify-between items-center mb-4">
          <div className="flex items-center space-x-2">
            <img
              src={article.sourceOutlet?.logo?.url || '/api/placeholder/32/32'}
              alt={article.sourceOutlet?.name}
              className="w-6 h-6 rounded-full"
            />
            <span className="text-sm text-slate-600">{article.sourceOutlet?.name}</span>
          </div>
          <span className={`text-sm font-medium ${getBiasColor(article.sourceOutlet?.biasRating)}`}>
            Bias Rating: {article.sourceOutlet?.biasRating}
          </span>
        </div>

        <h2 className="font-serif text-xl mb-3 text-slate-900 leading-tight">
          {article.pageName}
        </h2>

        <p className="text-slate-600 text-sm leading-relaxed mb-4">
          {article.summary}
        </p>

        <div className="flex justify-between items-center pt-4 border-t border-slate-100">
          <span className="text-sm text-slate-500">
            {new Date(article.publicationDate).toLocaleDateString()}
          </span>
          <div className="flex items-center space-x-2">
            <span className={`px-3 py-1 rounded-full text-xs ${
              article.sentimentType?.title === 'Positive' ? 'bg-emerald-100 text-emerald-700' :
              article.sentimentType?.title === 'Neutral' ? 'bg-blue-100 text-blue-700' :
              'bg-amber-100 text-amber-700'
            }`}>
              {article.sentimentType?.title}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ArticleCard;
