import React, { useState } from 'react';
import Navigation from './components/Navigation';
import Hero from './components/Hero';
import SentimentFilter from './components/SentimentFilter';
import FeaturedStory from './components/FeaturedStory';
import ArticleDisplay from './components/ArticleDisplay';
import Footer from './components/Footer';
import useArticles from './hooks/useArticles';
import './App.css';

function App() {
  const { articles, loading, error } = useArticles();
  const [selectedSentiment, setSelectedSentiment] = useState('good');

  const filteredArticles = articles?.filter(article => {
    const sentimentType = article.fields?.sentimentType?.fields?.title?.toLowerCase();

    // Debug logging
    console.log({
      title: article.fields?.pageName,
      sentiment: sentimentType,
      selectedFilter: selectedSentiment,
      wouldInclude: selectedSentiment === 'good' ? 
        'should show if positive' : 
        'should show if neutral/negative'
    });

    if (selectedSentiment === 'good') {
      return sentimentType === 'positive';
    }
    return sentimentType === 'neutral' || sentimentType === 'negative';
  });

  // Add overall state logging
  useEffect(() => {
    console.log('Filter State Changed:', {
      selectedSentiment,
      totalArticles: articles?.length,
      filteredCount: filteredArticles?.length
    });
  }, [selectedSentiment, articles, filteredArticles]);

  // Add debug logging for overall state
  console.log('Filter State:', {
    totalArticles: articles?.length,
    filteredCount: filteredArticles?.length,
    selectedSentiment,
  });

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-stone-100">
        <div className="text-xl text-slate-300">Loading stories of progress...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-stone-100">
        <div className="text-xl text-red-400">Error: {error}</div>
      </div>
    );
  }

  return (
    <div className="flex flex-col min-h-screen">
      <Navigation />
      <Hero />

      <div className="flex-grow">
        <div className="m-6 px-0">
          <SentimentFilter 
            selectedSentiment={selectedSentiment}
            setSelectedSentiment={setSelectedSentiment}
          />

          <div className="pt-4 pb-4">
            <div className="flex-grow">
              {articles[0] && <FeaturedStory article={articles[0]} />}
            </div>
          </div>

          <div>
            <ArticleDisplay articles={filteredArticles} />
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default App;
